/**
 *
 * @param e {Object} TouchData as generated by ember-mobile-core
 * @param element {HTMLElement} The element to which the coordinates need to be normalized.
 * @returns {{current: {x: number, y: number}, initial: {x: number, y: number}}}
 */
export default function normalizeCoordinates(e, element) {
  const bcr = element.getBoundingClientRect();

  return {
    ...e,
    initial: {
      ...e.initial,
      x: e.initial.x - bcr.x,
      y: e.initial.x - bcr.x
    },
    current: {
      ...e.current,
      x: e.current.x - bcr.x,
      y: e.current.x - bcr.x
    }
  };
}
